/* Sidebar Styles */
.sidebar {
    display: flex;
    flex-direction: column;
    height: 100%;
    padding: 20px;
    background-color: #fff;
    overflow-y: auto; /* Allow scrolling if content overflows */
    box-sizing: border-box;
  }
  
  .sidebar nav {
    flex-grow: 1;
  }
  
  .sidebar ul {
    list-style: none;
    padding: 0;
    margin: 0;
  }
  
  .sidebar nav ul li {
    margin-bottom: 10px;
  }
  
  .sidebar nav ul li a {
    display: block;
    padding: 10px 15px;
    font-size: 16px;
    color: #333;
    text-decoration: none;
    border-radius: 4px;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
  
  .sidebar nav ul li a:hover {
    background-color: #FF8438; /* Secondary Orange on hover */
    color: white;
  }
  
  .sidebar nav ul li a.active {
    background-color: #FC9256; /* Primary Orange for active state */
    color: white;
  }
  
  .sidebar-separator {
    border: 0;
    border-top: 1px solid #ccc;
    margin: 20px 0;
  }
  
  /* Bottom Section Styles */
  .sidebar > div ul li {
    margin-bottom: 10px;
  }
  
  .sidebar > div ul li a {
    display: block;
    padding: 10px 15px;
    font-size: 16px;
    text-decoration: none;
    border-radius: 4px;
    transition: background-color 0.3s ease, color 0.3s ease;
  }
  
  /* Specific styling for Logout */
  .sidebar > div ul li:nth-child(1) a {
    background-color: #FC9256; /* Primary Orange for Logout */
    color: white;
  }
  
  .sidebar > div ul li:nth-child(1) a:hover {
    background-color: #FF8438; /* Secondary Orange on hover */
  }
  
  /* Specific styling for Settings */
  .sidebar > div ul li:nth-child(2) a {
    background-color: #A0522D; /* Dark brown for Settings */
    color: white;
  }
  
  .sidebar > div ul li:nth-child(2) a:hover {
    background-color: #FF8438; /* Secondary Orange on hover */
  }
  
  /* Ensure the bottom section stays within the sidebar */
  .sidebar > div {
    margin-top: auto; /* Push the bottom section to the bottom */
  }
  
  /* Responsive Adjustments */
  @media (max-width: 768px) {
    .sidebar {
      padding: 15px;
      width: ;
    }
  
    .sidebar nav ul li a,
    .sidebar > div ul li a {
      font-size: 14px;
      padding: 8px 12px;
    }
  }
  
  @media (max-width: 480px) {
    .sidebar {
      padding: 10px;
    }
  
    .sidebar nav ul li a,
    .sidebar > div ul li a {
      font-size: 12px;
      padding: 6px 10px;
    }
  }
