+/* Base container styling */
+.profile-container {
+  display: flex;
+  flex-direction: column;
+  /* align-items: center; Removed align-items: center to allow content to take full width */
+  /* justify-content: center; Removed justify-content: center */
+  padding: 20px; /* Added overall padding */
+  margin: 0 auto; /* Center the container */
+  max-width: 1000px; /* Limit max width for better readability on large screens */
+  width: 100%; /* Ensure it takes full width up to max-width */
+  box-sizing: border-box; /* Include padding in width */
+  /* background-color: #f9fbfd; Light background */
+  border-radius: 12px;
+  transition: margin-left 0.3s ease-in-out;
+}
+
+/* Sidebar adjustments */
+.profile-container.sidebar-open {
+  margin-left: 90px;
+}
+
+.profile-container.sidebar-closed {
+  margin-left: 200px;
+}
+
+/* Header styling */
+.profile-view-header {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  padding: 10px 0; /* Adjusted padding */
+  margin-bottom: 30px; /* Increased margin below header */
+  border-bottom: 2px solid #ff9500;
+  width: 100%;
+}
+
+.header-main-title {
+  font-size: 1.8em; /* Slightly larger title */
+  color: #ff6200;
+  margin: 0;
+  text-align: center;
+  flex-grow: 1;
+  font-weight: 700; /* Make title bold */
+}
+
+.header-action-button {
+  border: 1px solid transparent;
+  padding: 8px 15px; /* Adjusted padding */
+  border-radius: 6px;
+  cursor: pointer;
+  font-size: 0.9em;
+  font-weight: 500;
+  transition: background-color 0.2s ease, color 0.2s ease, border-color 0.2s ease, transform 0.1s ease; /* Added transform transition */
+  white-space: nowrap;
+  margin: 0 5px; /* Add space between buttons */
+}
+
+.header-action-button:active {
+    transform: translateY(1px); /* Subtle press effect */
+}
+
+.back-button-header {
+  color: #ff9500;
+}
+
+.back-button-header:hover {
+  background-color: #fff3e0;
+  color: #e67e22;
+}
+
+.edit-button-header {
+  color: #ff9500;
+  border: 1px solid #ff9500;
+}
+
+.edit-button-header:hover {
+  background-color: #ff9500;
+  color: #fff;
+}
+
+.edit-button-header:disabled {
+    opacity: 0.6;
+    cursor: not-allowed;
+}
+
+
+/* Form styling */
+.profile-form {
+  display: flex;
+  flex-direction: column;
+  gap: 30px; /* Increased gap between sections */
+  width: 100%;
+}
+
+fieldset.form-section {
+  background-color: #ffffff; /* Ensures the fieldset content area is white */
+  border: 1px solid #cccccc; /* Defines a border for the fieldset */
+  /* Adjust padding: top padding should be larger to make space for the legend. */
+  padding: 1.8em 1.5em 1.5em 1.5em; /* Increased padding for more breathing room */
+  margin-bottom: 0; /* Remove margin-bottom here, gap handles spacing */
+  position: relative;
+  border-radius: 8px; /* Rounded corners for fieldset */
+  transition: box-shadow 0.3s ease, transform 0.3s ease; /* Added transition for hover */
+}
+
+fieldset.form-section:hover {
+  transform: translateY(-5px); /* More pronounced lift effect */
+  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.12); /* Stronger shadow on hover */
+}
+
+fieldset.form-section legend {
+  font-weight: bold;         /* Make text bold */
+  text-transform: uppercase; /* Make text uppercase */
+  color: #4A5C6F;           /* Use a professional, readable dark color */
+  background-color: #ffffff; /* Crucial: Match fieldset background to "sit inside" */
+  padding: 4px 12px;         /* Add padding around the legend text */
+  margin-left: 20px;         /* Indent legend from the left edge */
+  font-size: 0.95em;         /* Set a clear font size */
+  letter-spacing: 0.8px;     /* Increase letter spacing slightly */
